import { useState } from "react";
import React from "react";

var SignIn = () => {
    const [Email, setEmail] = useState("");
    const [Password, setPass] = useState("");
  
    const [emailErrorMessage, setEmailError] = useState("");
    const [passErrorMessage ,setPassError] = useState("");
  
    return(
        <form className="sigin-form" >
        <label htmlFor="email">Email /  UserName</label>
        <input
          type="text"
          id="email"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        ></input>
        <p id="emailAlert">{emailErrorMessage}</p>
        <label htmlFor="pass">Password</label>
        <input
          type="password"
          id="pass"
          onChange={(e) => {
            setPass(e.target.value);
          }}
        ></input>
        <p id="passAlert">{passErrorMessage}</p>
        <center>
          <button id="signin-btn" on>Sign In</button>
        </center>
      </form>

    )
};
export default SignIn;
